import React, { useState } from "react";
import './App.css';

function Johnny() {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [age, setAge] = useState('');
    const [school, setSchool] = useState('');
    const [bestSubject, setBestSubject] = useState('');
    const [worstSubject, setWorstSubject] = useState('');
    const [parentsEmail, setParentsEmail] = useState('');
    const [parentsPhoneNumber, setParentsPhoneNumber] = useState('');
    const [students, setStudents] = useState([]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const newStudent = {
            firstName,
            lastName,
            age,
            school,
            bestSubject,
            worstSubject,
            parentsEmail,
            parentsPhoneNumber
        };
        setStudents([...students, newStudent]);
        // Reset the form fields after submission
        setFirstName('');
        setLastName('');
        setAge('');
        setSchool('');
        setBestSubject('');
        setWorstSubject('');
        setParentsEmail('');
        setParentsPhoneNumber('');
    };

    return (
        <div className="johnny">
            <h1>Sign Up / Login</h1>
            <div style={{display:'flex'}}>
            <form onSubmit={handleSubmit}>
                <label>First Name:</label>
                <input type="text" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                <label>Last Name:</label>
                <input type="text" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                <label>Age:</label>
                <input type="number" value={age} onChange={(e) => setAge(e.target.value)} required />
                <label>School:</label>
                <input type="text" value={school} onChange={(e) => setSchool(e.target.value)} required />
                <label>Best Subject:</label>
                <input type="text" value={bestSubject} onChange={(e) => setBestSubject(e.target.value)} required />
                <label>Worst Subject:</label>
                <input type="text" value={worstSubject} onChange={(e) => setWorstSubject(e.target.value)} required />
                <label>Parents' Email:</label>
                <input type="email" value={parentsEmail} onChange={(e) => setParentsEmail(e.target.value)} required />
                <label>Parents' Phone Number:</label>
                <input type="tel" value={parentsPhoneNumber} onChange={(e) => setParentsPhoneNumber(e.target.value)} required />
                <button type="submit">Submit</button>
            </form></div>
            <h2>All Students:</h2>
            <ul>
                {students.map((student, index) => (
                    <li key={index}>
                        {student.firstName} {student.lastName}, Age: {student.age}, School: {student.school}
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default Johnny;
