import React, { useState } from "react";
import './App.css';

function Tommy({ user }) {
  const [schedule, setSchedule] = useState([
    { day: 'Monday', time: '3 PM', reserved: false },
    { day: 'Monday', time: '4 PM', reserved: false },
    { day: 'Monday', time: '5 PM', reserved: false },
    { day: 'Monday', time: '6 PM', reserved: false },
    { day: 'Tuesday', time: '3 PM', reserved: false },
    { day: 'Tuesday', time: '4 PM', reserved: false },
    { day: 'Tuesday', time: '5 PM', reserved: false },
    { day: 'Tuesday', time: '6 PM', reserved: false },
    { day: 'Wednesday', time: '3 PM', reserved: false },
    { day: 'Wednesday', time: '4 PM', reserved: false },
    { day: 'Wednesday', time: '5 PM', reserved: false },
    { day: 'Wednesday', time: '6 PM', reserved: false },
    { day: 'Thursday', time: '3 PM', reserved: false },
    { day: 'Thursday', time: '4 PM', reserved: false },
    { day: 'Thursday', time: '5 PM', reserved: false },
    { day: 'Thursday', time: '6 PM', reserved: false },
    { day: 'Friday', time: '3 PM', reserved: false },
    { day: 'Friday', time: '4 PM', reserved: false },
    { day: 'Friday', time: '5 PM', reserved: false },
    { day: 'Friday', time: '6 PM', reserved: false },
  ]);

  const handleReservation = (day, time) => {
    const updatedSchedule = schedule.map(slot =>
      slot.day === day && slot.time === time ? { ...slot, reserved: !slot.reserved } : slot
    );
    setSchedule(updatedSchedule);
  };

  return (
    <div className="tommy">
      <h1 style={{ marginTop: '0%' }}>3</h1>
      <h2>Content coming soon!</h2>
      <h2>Schedule:</h2>
      <div className="schedule">
        <table>
          <thead>
            <tr>
              <th>Day</th>
              <th>Time</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {schedule.map(({ day, time, reserved }) => (
              <tr key={`${day}-${time}`}>
                <td>{day}</td>
                <td>{time}</td>
                <td>
                  <button
                    onClick={() => handleReservation(day, time)}
                    disabled={user && reserved}
                  >
                    {reserved ? "Reserved" : "Available"}
                  </button>


                  <div style={{ display: 'inline-block', marginBottom: '5%' }} className="custom-checkbox">
                        <input
                          type='checkbox'
                          id={`${day}-${time}`}
                          checked={reserved}
                          className='status'
                          onChange={() => handleReservation(day, time)}
                        //   disabled={user && slot.reserved}
                        />
                        <label htmlFor={`${day}-${time}`} className={`status-switch ${reserved ? 'checked' : ''}`}>
                          {reserved ? 'Reserved' : 'Available'}
                        </label>
                      </div>




                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Tommy;
