import React, { useState } from "react";
import './App.css';

function Tommy({ user }) {
  const weeks = [
    {
      Monday: {
        "3 PM": false,
        "4 PM": false,
        "5 PM": false,
        "6 PM": false,
        "7 PM": false,
      },
      Tuesday: {
        "3 PM": false,
        "4 PM": false,
        "5 PM": false,
        "6 PM": false,
        "7 PM": false,
      },
      Wednesday: {
        "3 PM": false,
        "4 PM": false,
        "5 PM": false,
        "6 PM": false,
        "7 PM": false,
      },
      Thursday: {
        "3 PM": false,
        "4 PM": false,
        "5 PM": false,
        "6 PM": false,
        "7 PM": false,
      },
      Friday: {
        "3 PM": false,
        "4 PM": false,
        "5 PM": false,
        "6 PM": false,
        "7 PM": false,
      },
    },
    // Add more weeks here
  ];

  const [currentWeekIndex, setCurrentWeekIndex] = useState(0);
  const currentWeek = weeks[currentWeekIndex];

  const setWeeks = updatedWeeks => {
    const updatedWeekList = [...weeks];
    updatedWeekList[currentWeekIndex] = updatedWeeks;
    setWeeks(updatedWeekList);
  };

  const handleReservation = (day, time) => {
    const updatedWeek = { ...currentWeek };
    updatedWeek[day][time] = !updatedWeek[day][time];
    setWeeks(updatedWeek);
  };

  const handleNextWeek = () => {
    setCurrentWeekIndex(prevIndex => (prevIndex + 1) % weeks.length);
  };

  const handlePrevWeek = () => {
    setCurrentWeekIndex(prevIndex => (prevIndex - 1 + weeks.length) % weeks.length);
  };

  return (
    <div className="tommy">
      <h1 style={{ marginTop: '0%' }}>{currentWeekIndex}</h1>
      <h2>Schedule</h2>
      <table style={{ backgroundColor: '#282c34', color: 'white', minWidth: '100%' }}>
        <thead>
          <tr>
            <th>Time</th>
            {Object.keys(currentWeek).map(day => <th key={day}>{day}</th>)}
          </tr>
        </thead>
        <tbody>
          {Object.keys(currentWeek.Monday).map(time => (
            <tr key={time}>
              <td>{time}</td>
              {Object.keys(currentWeek).map(day => (
                <td key={`${day}-${time}`}>
                  <button
                    className={`reservation-button ${currentWeek[day][time] ? 'reserved' : ''}`}
                    onClick={() => handleReservation(day, time)}
                    disabled={user && currentWeek[day][time]}
                    style={{
                      padding: '20px',
                      backgroundColor: currentWeek[day][time] ? 'red' : 'green',
                      color: 'white',
                    }}
                  >
                    {currentWeek[day][time] ? 'Reserved' : 'Available'}
                  </button>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div>
        <button onClick={handlePrevWeek}>Previous Week</button>
        <button onClick={handleNextWeek}>Next Week</button>
      </div>
    </div>
  );
}

export default Tommy;
