import React, { useState } from "react";
import './App.css';

function Tommy({ user }) {
  const initialSchedule = {
    Monday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Tuesday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Wednesday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Thursday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Friday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
  };

  const [weeks, setWeeks] = useState([initialSchedule, initialSchedule, initialSchedule, initialSchedule, initialSchedule]);
  const [currentWeekIndex, setCurrentWeekIndex] = useState(1);

  const handleReservation = (day, time) => {
    const updatedWeeks = [...weeks];
    updatedWeeks[currentWeekIndex][day][time] = !updatedWeeks[currentWeekIndex][day][time];
    setWeeks(updatedWeeks);
  };

  const switchToWeek = (index) => {
    setCurrentWeekIndex(index);
  };

  return (
    <div className="tommy">
      <h1 style={{ marginTop: '0%' }}>{currentWeekIndex}</h1>
      <h2>Schedule</h2>
      <div>
        <button onClick={() => switchToWeek(1)}>Week 1</button>
        <button onClick={() => switchToWeek(2)}>Week 2</button>
        <button onClick={() => switchToWeek(3)}>Week 3</button>
        <button onClick={() => switchToWeek(4)}>Week 4</button>
        <button onClick={() => switchToWeek(5)}>Week 5</button>
      </div>
      <table style={{ backgroundColor: '#282c34', color: 'white', minWidth: '100%' }}>
        <thead>
          <tr>
            <th>Time</th>
            {Object.keys(weeks[{currentWeekIndex}]).map(day => <th key={day}>{day}</th>)}
          </tr>
        </thead>
        <tbody>
          {Object.keys(weeks[0].Monday).map(time => (
            <tr key={time}>
              <td>{time}</td>
              {weeks.map((week, index) => (
                <React.Fragment key={index}>
                  {Object.keys(week).map(day => (
                    <td key={`${day}-${time}`}>
                      <button
                        className={`reservation-button ${week[day][time] ? 'reserved' : ''}`}
                        onClick={() => handleReservation(day, time)}
                        disabled={user && week[day][time]}
                        style={{
                          padding: '20px',
                          backgroundColor: week[day][time] ? 'red' : 'green',
                          color: 'white',
                        }}
                      >
                        {week[day][time] ? 'Reserved' : 'Available'}
                      </button>
                    </td>
                  ))}
                </React.Fragment>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Tommy;
