import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [showDropdown, setShowDropdown] = useState(false);

  const handleAppleClick = () => {
    setShowDropdown(!showDropdown);
  };

  const handleOptionClick = (option) => {
    // Implement the functionality for each option here if needed
    console.log(`Option "${option}" clicked.`);
    setShowDropdown(false);
  };
  console.log(showDropdown)

  return (
    <div className="App">
      <header className="App-header">
        <h2>Welcome to</h2>
        <h1 style={{ color: 'yellow', marginTop: '-2%', marginBottom: '0%' }}>
          Trish the Teacher !
        </h1>
        <div style={{ height: '200px' }} className="teach"></div>

        <p>Website currently under construction</p>
        <p style={{ color: 'yellow' }}>to see the menu</p>
        <p style={{ color: 'yellow' }}>Click the Apple!</p>
        <div
          style={{ height: '200px' }}
          className="apple"
          onClick={handleAppleClick}
        ></div>
        {/* Dropdown Menu */}
        {showDropdown && (
          <div style={{backgroundColor:'darkred',padding:'5%',minWidth:'100%'}} className="dropdown-menu">
            <div style={{backgroundColor:'white',color:'blue', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'5% 100px'}} onClick={() => handleOptionClick('$')}>Option 1</div>
            <div style={{backgroundColor:'white',color:'purple', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'5% 100px'}}onClick={() => handleOptionClick('Option 1')}>Option 2</div>
            <div style={{backgroundColor:'white',color:'yellow', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'5% 100px'}}onClick={() => handleOptionClick('Option 2')}>Option 3</div>
            <div style={{backgroundColor:'white',color:'green', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'5% 100px'}}onClick={() => handleOptionClick('Option 3')}>Option 4</div>
            <div style={{backgroundColor:'white',color:'red', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'5% 100px'}} onClick={() => setShowDropdown(false)}>X</div>
          </div>
        )}


        <p style={{ color: 'yellow' }}>Come back soon!</p>


      </header>
    </div>
  );
}

export default App;
