import React, { useState } from "react";
import './App.css';

function Tommy({ user }) {
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];

  const initialSchedule = {
    Monday: [
      { time: '3 PM', reserved: false },
      { time: '4 PM', reserved: false },
      { time: '5 PM', reserved: false },
      { time: '6 PM', reserved: false },
      { time: '7 PM', reserved: false },
    ],
    Tuesday: [
      { time: '3 PM', reserved: false },
      { time: '4 PM', reserved: false },
      { time: '5 PM', reserved: false },
      { time: '6 PM', reserved: false },
      { time: '7 PM', reserved: false },
    ],
    Wednesday: [
      { time: '3 PM', reserved: false },
      { time: '4 PM', reserved: false },
      { time: '5 PM', reserved: false },
      { time: '6 PM', reserved: false },
      { time: '7 PM', reserved: false },
    ],
    Thursday: [
      { time: '3 PM', reserved: false },
      { time: '4 PM', reserved: false },
      { time: '5 PM', reserved: false },
      { time: '6 PM', reserved: false },
      { time: '7 PM', reserved: false },
    ],
    Friday: [
      { time: '3 PM', reserved: false },
      { time: '4 PM', reserved: false },
      { time: '5 PM', reserved: false },
      { time: '6 PM', reserved: false },
      { time: '7 PM', reserved: false },
    ],
  };

  const [schedule, setSchedule] = useState(initialSchedule);

  const handleReservation = (day, time) => {
    const updatedSchedule = {
      ...schedule,
      [day]: schedule[day].map(slot =>
        slot.time === time ? { ...slot, reserved: !slot.reserved } : slot
      ),
    };
    setSchedule(updatedSchedule);
  };

  return (
    <div className="tommy">
      <h1 style={{ marginTop: '0%' }}>3</h1>
      <h2>Content coming soon!</h2>
      <h2>Schedule:</h2>
      <div className="schedule">
        <table>
          <thead>
            <tr>
              <th>Time</th>
              {daysOfWeek.map(day => <th key={day}>{day}</th>)}
            </tr>
          </thead>
          <tbody>
            {schedule.Monday.map(({ time, reserved }) => (
              <tr key={time}>
                <td>{time}</td>
                {daysOfWeek.map(day => (
                  <td key={`${day}-${time}`}>
                    <button
                      onClick={() => handleReservation(day, time)}
                      disabled={user && reserved}
                    >
                      {reserved ? "Reserved" : "Available"}
                    </button>
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Tommy;
