import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [showDropdown, setShowDropdown] = useState(false);

  const handleAppleClick = () => {
    setShowDropdown(!showDropdown);
  };

  const handleOptionClick = (option) => {
    // Implement the functionality for each option here if needed
    console.log(`Option "${option}" clicked.`);
    setShowDropdown(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Welcome to</h2>
        <h1 style={{ color: 'yellow', marginTop: '-2%', marginBottom: '0%' }}>
          Trish the Teacher !
        </h1>
        <div style={{ height: '200px' }} className="teach"></div>

        <p>Website currently under construction</p>
        <p style={{ color: 'yellow' }}>Sorry!</p>
        <div
          style={{ height: '200px' }}
          className="apple"
          onClick={handleAppleClick}
        ></div>
        <p style={{ color: 'yellow' }}>Come back soon!</p>

        {/* Dropdown Menu */}
        {showDropdown && (
          <div className="dropdown-menu">
            <div onClick={() => handleOptionClick('$')}>Option $</div>
            <div onClick={() => handleOptionClick('Option 1')}>Option 1</div>
            <div onClick={() => handleOptionClick('Option 2')}>Option 2</div>
            <div onClick={() => handleOptionClick('Option 3')}>Option 3</div>
            <div onClick={() => setShowDropdown(false)}>Hide</div>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
