import React, { useState } from 'react';
import { NavLink } from "react-router-dom";




function NavBar() {
  const [showDropdown, setShowDropdown] = useState(false);

  const handleAppleClick = () => {
    setShowDropdown(!showDropdown);
  };

  const handleOptionClick = (option) => {
    // Implement the functionality for each option here if needed
    console.log(`Option "${option}" clicked.`);

    setShowDropdown(false);
  };
  console.log(showDropdown)

  return (
    <div className="App">
      <header className="App-header">

        <h1 style={{ color: 'white',  marginBottom: '0%' }}>
          Trish the Teacher !
        </h1>

        <div
          style={{ height: '200px' }}
          className="apple"
          onClick={handleAppleClick}
        ></div><p style={{ color: 'yellow' }}>Click the Apple for the <strong>Menu!</strong></p>
        {/* Dropdown Menu */}
        {showDropdown && (
          <div style={{backgroundColor:'darkred',padding:'5%',minWidth:'100%'}} className="dropdown-menu">
            <div style={{backgroundColor:'white',color:'blue', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'3% 100px'}} onClick={() => handleOptionClick('$')}><NavLink exact to="/johnny">Johnny</NavLink></div>
            <div style={{backgroundColor:'white',color:'purple', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'3% 100px'}}onClick={() => handleOptionClick('Option 1')}><NavLink exact to="/joey">Joey</NavLink></div>
            <div style={{backgroundColor:'white',color:'yellow', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'3% 100px'}}onClick={() => handleOptionClick('Option 2')}><NavLink exact to="/tommy">Tommy</NavLink></div>
            <div style={{backgroundColor:'white',color:'green', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'3% 100px'}}onClick={() => handleOptionClick('Option 3')}><NavLink exact to="/elijah">Elijah</NavLink></div>
            <div style={{backgroundColor:'white',color:'red', border:'2px solid black',cursor:'pointer', margin:'5%',padding:'3% 100px'}} onClick={() => setShowDropdown(false)}>X</div>
          </div>
        )}




      </header>
    </div>
  );
}

export default NavBar;
