import React, { useState } from "react";
import './App.css';

function Tommy({ user }) {
  const [schedule, setSchedule] = useState({
    Monday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Tuesday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Wednesday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Thursday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
    Friday: {
      "3 PM": false,
      "4 PM": false,
      "5 PM": false,
      "6 PM": false,
      "7 PM": false,
    },
  });

  const handleReservation = (day, time) => {
    const updatedSchedule = { ...schedule };
    updatedSchedule[day][time] = !updatedSchedule[day][time];
    setSchedule(updatedSchedule);
  };

  return (
    <div className="tommy">
      <h1 style={{ marginTop: '0%' }}>3</h1>
      <h2>Schedule</h2>
      <table style={{ backgroundColor: '#282c34', color: 'white', minWidth: '100%' }}>
        <thead>
          <tr>
            <th>Time</th>
            {Object.keys(schedule).map(day => <th key={day}>{day}</th>)}
          </tr>
        </thead>
        <tbody>
          {Object.keys(schedule.Monday).map(time => (
            <tr key={time}>
              <td>{time}</td>
              {Object.keys(schedule).map(day => (
                <td key={`${day}-${time}`}>
                  <button
                    className={`reservation-button ${schedule[day][time] ? 'reserved' : ''}`}
                    onClick={() => handleReservation(day, time)}
                    disabled={user && schedule[day][time]}
                    style={{
                      padding: '20px',
                      backgroundColor: schedule[day][time] ? 'red' : 'green',
                      color: 'white',
                    }}
                  >
                    {schedule[day][time] ? 'Reserved' : 'Available'}
                  </button>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Tommy;
