import React, { useState } from "react";
import './App.css';

function Tommy({ user }) {
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];

  // Initialize the schedule with each day having its own time slots
  const initialSchedule = daysOfWeek.map(day => ({
    day,
    slots: [
      { time: '3 PM', reserved: false },
      { time: '4 PM', reserved: false },
      { time: '5 PM', reserved: false },
      { time: '6 PM', reserved: false },
      { time: '7 PM', reserved: false },
    ]
  }));

  const [schedule, setSchedule] = useState(initialSchedule);

  const handleReservation = (day, time) => {
    const updatedSchedule = schedule.map(item =>
      item.day === day
        ? {
            ...item,
            slots: item.slots.map(slot =>
              slot.time === time ? { ...slot, reserved: !slot.reserved } : slot
            )
          }
        : item
    );
    setSchedule(updatedSchedule);
  };

  return (
    <div className="tommy">
      <h1 style={{ marginTop: '0%' }}>3</h1>
      <h2>Content coming soon!</h2>
      <h2>Schedule:</h2>
      <div className="schedule">
        <table>
          <thead>
            <tr>
              <th>Time</th>
              {daysOfWeek.map(day => <th key={day}>{day}</th>)}
            </tr>
          </thead>
          <tbody>
            {schedule[0].slots.map(({ time }) => (
              <tr key={time}>
                <td>{time}</td>
                {schedule.map(({ day, slots }) => (
                  <td key={`${day}-${time}`}>
                    <button
                      onClick={() => handleReservation(day, time)}
                      disabled={user && slots.find(slot => slot.time === time && slot.reserved)}
                    >
                      {slots.find(slot => slot.time === time && slot.reserved)
                        ? "Reserved"
                        : "Available"}
                    </button>
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Tommy;
